import { Component, OnInit, Input, ViewChild, ElementRef } from '@angular/core';
import { AbstractControl, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { DataService } from '../data.service';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent implements OnInit {
  english: boolean = false;
  text1: string = '';
  text2: string = '';
  text3: string = '';
  text4: string = '';

  submitted = false;

  form: FormGroup = new FormGroup({
    message: new FormControl(''),
    username: new FormControl(''),
    email: new FormControl(''),
  });


  constructor(private dataService: DataService, private formBuilder: FormBuilder) { 
    this.changeText();
  }

  ngOnInit() {
    this.dataService
      .langChanged
      .subscribe(() => {
        this.english = this.dataService.get();
        this.changeText();
      }),
      this.form = this.formBuilder.group(
        {
          message: ['', [Validators.required,  Validators.minLength(6)]],
          username: [
            '',
            [
              Validators.required,
              Validators.pattern('[a-zA-Z ]*'),
              Validators.minLength(6),
              Validators.maxLength(20)
            ]
          ],
          email: ['', Validators.compose([
            Validators.required,
            Validators.pattern("[a-z0-9._%+-]+@[a-z0-9.-]+[\.]([a-z]){2,4}$")])],
        },
      );
  }

  get f(): { [key: string]: AbstractControl } {
    return this.form.controls;
  }
  @ViewChild('myForm') myForm: ElementRef | undefined;
  @ViewChild('sendButton') sendButton: ElementRef | undefined;

  async onSubmit(): void {
    // action="https://valer-neufeld.developerakademie.net/send_mail/send_mail.php"
    this.submitted = true;
    console.log('Fertig', this.myForm);
    let sendButton = this.sendButton?.nativeElement;
    sendButton.disabled = true;

    let fd = new FormData();
    fd.append('name', this.form.value.name);
    fd.append('message', this.form.value.message);
    await fetch('https://valer-neufeld.developerakademie.net/send_mail/send_mail.php')
    {
      method: 'POST',
      body: fd
    }

    if (this.form.invalid) {
      return;
    }

    console.log(JSON.stringify(this.form.value, null, 2));
    console.log(this.form.value.message);
  }

  onReset(): void {
    this.submitted = false;
    this.form.reset();
  }

  changeText() {
    let en: boolean = this.english;
    this.text1 = en ? `Contact me` : `Kontakt`;
    this.text2 = en ? `Get in touch via the form below, or by emailing` : `Kontaktieren Sie mich über das unten stehende Formular oder per E-Mail: `;
    this.text3 = en ? `Message` : `Nachricht`;
    this.text4 = en ? `Send message` : `Nachricht senden`;
  }
}
<section class="contact" id="contact">
  <h1>{{ text1 }}</h1>
  <p>
    {{ text2 }} <a href="mailto:valer.neufeld@gmx.de"> valer.neufeld@gmx.de</a>
  </p>

  <div class="register-form">
    <form [formGroup]="form" (ngSubmit)="onSubmit()" #myForm>
      <div class="form-group">
        <label>Name</label>
        <input
          name="name"
          type="text"
          formControlName="username"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['username'].errors }"
        />
        <div *ngIf="submitted && f['username'].errors" class="invalid-feedback">
          <div *ngIf="f['username'].errors['required'] && english">
            Name is required
          </div>
          <div *ngIf="f['username'].errors['required'] && !english">
            Name ist erforderlich
          </div>
          <div *ngIf="f['username'].errors['minlength'] && english">
            Username must be at least 6 characters
          </div>
          <div *ngIf="f['username'].errors['minlength'] && !english">
            Mindestens 6 Zeichen
          </div>
          <div *ngIf="f['username'].errors['pattern'] && english">
            Only Letters allow.
          </div>
          <div *ngIf="f['username'].errors['pattern'] && !english">
            Nur Buchstaben erlaubt
          </div>
        </div>
      </div>

      <div class="form-group">
        <label>Email</label>
        <input
          name="email"
          type="text"
          formControlName="email"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['email'].errors }"
        />
        <div *ngIf="submitted && f['email'].errors" class="invalid-feedback">
          <div *ngIf="f['email'].errors['required'] && english">
            Email is required
          </div>
          <div *ngIf="f['email'].errors['required'] && !english">
            Email ist erforderlich
          </div>
          <div *ngIf="f['email'].errors['pattern'] && english">
            Email is invalid
          </div>
          <div *ngIf="f['email'].errors['pattern'] && !english">
            Email hat ungültiges Format
          </div>
        </div>
      </div>

      <div class="form-group">
        <label>{{ text3 }}</label>
        <textarea
          name="message"
          type="text"
          formControlName="message"
          class="form-control"
          [ngClass]="{ 'is-invalid': submitted && f['message'].errors }"
        ></textarea>
        <div *ngIf="submitted && f['message'].errors" class="invalid-feedback">
          <div *ngIf="f['message'].errors['required'] && english">
            Message is required
          </div>
          <div *ngIf="f['message'].errors['required'] && !english">
            Dieses Feld muss gefüllt sein
          </div>
          <div *ngIf="f['message'].errors['minlength'] && english">
            Message must be at least 6 characters
          </div>
          <div *ngIf="f['message'].errors['minlength'] && !english">
            Gib mindestens 6 Zeichen ein
          </div>
        </div>
      </div>

      <div class="form-group">
        <button type="submit" #sendButton>
          <span class="text">{{ text4 }}</span>
        </button>
      </div>
    </form>
  </div>
</section>
